mapper.select:
SELECT B.* FROM ( SELECT ROWNUM RNUM, A.* FROM ( SELECT BO_NO, BO_TITLE, BO_WRITER , BO_DATE, BO_HIT FROM FREEBOARD
ORDER BY BO_NO DESC ) A ) B
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

mapper.insert:

	SELECT FREEBOARD_SEQ.NEXTVAL
	FROM DUAL

INSERT INTO FREEBOARD
(
	BO_NO
	, BO_TITLE
	, BO_WRITER
	, BO_IP
	, BO_MAIL
	, BO_PASS
	, BO_CONTENT
	, ATCH_FILE_ID
)VALUES(
	#{boNo,jdbcType=NUMERIC}
	, #{boTitle,jdbcType=VARCHAR}
	, #{boWriter,jdbcType=VARCHAR}
	, #{boIp,jdbcType=VARCHAR}
	, #{boMail,jdbcType=VARCHAR}
	, #{boPass,jdbcType=VARCHAR}
	, #{boContent,jdbcType=CLOB}
	, #{atchFileId,jdbcType=NUMERIC}
)

mapper.update:
UPDATE FREEBOARD
SET
	BO_MAIL = #{boMail,jdbcType=CLOB}
	, BO_CONTENT = #{boContent,jdbcType=CLOB}
	, ATCH_FILE_ID = #{atchFileId,jdbcType=NUMERIC}
WHERE BO_NO = #{boNo}
